import React from 'react'
import uuid from 'uuid/v4';

const posts = (state = [], action) => {
    switch (action.type) {
        case 'SEND_FORM':
            console.log(state);
            if (action.input && action.text) {
                return [
                    ...state,
                    {
                        id: uuid(),
                        title: action.input,
                        text: action.text
                    }
                ];
            }
            return state;
        case 'DELETE_POST':
            return state.filter(post => post.id !== action.id);
        case 'UPDATE_POST':
            console.log('UPDATE_POST');
            console.log(state);
           // console.log(action);
            if (action.optionalState) {
                // state.forEach(function (element) {
                //     console.log(element);
                //     if (element.id === action.optionalState.id){
                //         element.text = action.optionalState.text;
                //         element.title = action.optionalState.title;
                //     }
                // });
                state = state.filter(post => post.id !== action.optionalState.id);
                console.log(state);
                state =  [
                    ...state,
                    {
                        id: action.optionalState.id,
                        title: action.optionalState.title,
                        text: action.optionalState.text
                    }
                ];
                console.log(state);
                return  state;
                // let updatedPost = state.filter(post => post.id === action.optionalState.id);

               // console.log(action.optionalState.id);
                return [
                    ...state
                ];
            }
        default:
            return state
    }
};

export default posts
