import React from 'react'

const form = (state = [], action) => {
    switch (action.type) {
        case 'VALIDATE_REGISTER_FORM':
            return [{
                showElement: 'form-element-1',
                elementAction: 'updatePost',
                optionalState: {
                    id: action.id,
                    title: action.title,
                    text: action.text
                },
                enabled: true
            }];
        default:
            return state
    }
};

export default form
