import { connect } from 'react-redux'
import { sendForm } from '../actions'

import Form from '../components/Form'

// get state from redux and map to properties of component
// check is filter active or not
const mapStateToProps = (state,ownProps) => ({
     //enabled: ownProps.enabled === state.enabled,
    status:  getButtonStatus(state),
    input: ownProps.inputValue,
    area: ownProps.area
});

function getButtonStatus(state) {
   return [...state.button].pop();
}

const mapDispatchToProps = (dispatch,ownProps) => ({
    onClick: () => dispatch(sendForm(ownProps))
});

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(Form)
