import React from 'react'

const button = (state = [], action) => {
  //  let elementButton = state.filter(b => b.showElement === action.showElement).pop();
   // console.log(elementButton);
    console.log(action);
    console.log(state);
    switch (action.type) {
        case 'SHOW_FORM':
            console.log('SHOW_FORM');
            let elementButton = state.filter(b => b.showElement === action.showElement).pop();
            if (elementButton) {
                console.log(state);
                console.log(elementButton);
                return [{
                    showElement: action.showElement,
                    elementAction: 'create',
                    enabled: !elementButton.enabled
                }];
            }
            console.log(state);
            return [...state,
                {
                    showElement: action.showElement,
                    elementAction: 'create',
                    enabled: true
                }
            ];
        case 'SHOW_UPDATE_FORM':
            console.log('SHOW_UPDATE_FORM');
                console.log(state);
                console.log(action);
                return [{
                    showElement: 'form-element-1',
                    elementAction: 'update',
                    optionalState: [action.id, action.title, action.text],
                    enabled: true
                }];
        default:
            return state
    }
};

export default button
