import React, {useState} from 'react'
import { Button as UiButton, Confirm } from 'semantic-ui-react'
import RegisterForm from './RegisterForm'

function StartPage () {
    const [open, setOpen] = useState(false);
    const [result, setResult] = useState('show the modal to capture a result');

    return (
            <div>
                <p>
                    Result: <em>{result}</em>
                </p>
                <UiButton onClick={() => setOpen('true')}>Show</UiButton>
                <Confirm
                    open={open}
                    content= {RegisterForm}
                    onCancel={() => {setResult('cancel');setOpen(false)}}
                    onConfirm={() => {setResult('confirm');setOpen(false)}}
                />
            </div>
        )

}

export default StartPage
