import React from 'react'
import {connect} from "react-redux";
import {showPostDetail} from "../actions";
import {Popup, Button as UiButton, Grid} from 'semantic-ui-react'
import Button from '../containers/Buttons/Button';
import {Link} from "react-router-dom";

/** Post component */
function Post({title, text, id}) {
    let data = {'title': title, 'text': text, 'id': id};
    return (
        <div className={'ui segment'}>
            <h4 id={'post-title'}>{title}</h4>
            <p id={'post-text-content'}>{text}</p>
            <div id={'post-buttons'}>
                <Button
                    action={'deletePost'}
                    buttonText={'DELETE'}
                    id={id}
                />
                <UpdateShowPopup data={data}/>
            </div>
        </div>
    );
}

/** Button with popup options */
function UpdateShowPopup({dispatch,data}) {
    return (
        <Popup
            trigger={<UiButton>. . .</UiButton>}
            flowing hoverable>
            <Grid centered divided columns={2}>
                <Grid.Column textAlign='center'>
                    <Button
                        action={'showUpdateForm'}
                        buttonText={'UPDATE'}
                        classes={'ui right floated  button'}
                        title={data.title}
                        text={data.text}
                        id={data.id}
                    />
                </Grid.Column>
                <Grid.Column textAlign='center'>
                    <Link to="/post">
                        <UiButton
                            onClick={e => {
                                e.preventDefault();
                                dispatch(showPostDetail(data.id, data.title, data.text));
                            }}
                        >
                            SHOW</UiButton>
                    </Link>
                </Grid.Column>
            </Grid>
        </Popup>
    );
}


export default connect()(Post)
