export const showForm = (showElement) => ({
    type: 'SHOW_FORM',
    showElement,
});

export const sendForm = (input,area) => ({
    type: 'SEND_FORM',
    input,
    area,
});

export const deletePost = (id) => ({
    type: 'DELETE_POST',
    id
});

export const showUpdateForm = (id,title,text) => ({
    type: 'SHOW_UPDATE_FORM',
    id,
    title,
    text
});

export const updatePost = (optionalState) => ({
    type: 'UPDATE_POST',
    optionalState,
});
