import React, {useEffect, useState} from 'react'
import {Form, Input} from 'semantic-ui-react'


function RegisterForm() {

    // const [validation, setValidation] = useState('');
    // const [firstName, setFirstName] = useState('');
    // const [lastName, setLastName] = useState('');
    // const [password, setPassword] = useState('');
    const [formState, setFormState] = useState([]);
    const [checkboxAccepted, setCheckboxAccepted] = useState(false);

    // useEffect(() => {
    //     if (!validation) {
    //         console.log('s');
    //         return <Component.type {...Component.props} {...extraProps} />;
    //     }
    // }, [validation]);
    console.log(formState);
    console.log(formState.filter(el => el.key === 'firstName').pop().valid);
    //console.log(checkboxAccepted);

    function addValue(key, value) {
        setFormState(
            [...formState.filter(el => el.key !== key),
                {
                    key: key,
                    value: value,
                    valid: validate(key, value)
                }
            ]
        );
    }

    function validate(key, value) {

        switch (key) {
            case 'firstName':
                if (value.length < 5) {
                    return false;
                }
                return true;
            case 'lastName':
                if (value.length < 2) {
                    return false;
                }
                return true;
            case 'password':
                if (value.length < 1) {
                    return false;
                }
                return true;
            default:
                return true
        }
    }

    console.log(formState);

    return (
        <Form id={'register-ui-form'}>
            <Form.Group widths='equal'>
                <Form.Field
                    id='form-input-control-first-name'
                    control={Input}
                    //error={validate('firstName')}
                    // value={formState['firstName']}
                    onChange={e => addValue('firstName', e.target.value)}
                    label='First name'
                    placeholder='First name'
                />
                <Form.Field
                    id='form-input-control-last-name'
                    // error={validate('lastName')}
                    control={Input}
                    // value={formState['lastName']}
                    onChange={e => addValue('lastName', e.target.value)}
                    //onChange={e => validateRegisterForm(lastName, e.target.value)}
                    label='Last name'
                    placeholder='Last name'
                />
            </Form.Group>
            <Form.Group>
                <Form.Field
                    id='form-input-control-last-name'
                    // error={validate('password')}
                    control={Input}
                    //value={formState['password']}
                    onChange={e => addValue('password', e.target.value)}
                    label='password'
                    placeholder='password'
                />
            </Form.Group>
            <Form.Field>
                <Form.Checkbox
                    onClick={() => setCheckboxAccepted(!checkboxAccepted)}
                    error={{
                        content: 'You must agree to the terms and conditions',
                        pointing: 'left'
                    }}
                    label='I agree to the Terms and Conditions'
                />
            </Form.Field>
            <Form.Button content='Submit'/>
        </Form>
    )
}

export default RegisterForm
