import React , { useState, useEffect }  from 'react'
import { sendForm } from '../actions'

function Form({dispatch,status = {enabled: false},onClick}) {
    const [inputValue, setinputValue] = useState('');
    console.log(inputValue);
    return (
        <div className={'form' + " " + (status.enabled ? 'normal' : 'hidden')}>
                <br/>
                <div>
                    <input value={inputValue} ref={node => (input = node)}/>
                </div>
                <br/>
                <textarea/>
                <div>
                    <button
                        type="submit"
                        // onClick={onClick}
                        onClick={ e => {
                            e.preventDefault();
                            dispatch(sendForm(input.value));
                            input.value = ''
                        }}
                    >Create</button>
                </div>
        </div>
    );
}

export default Form
