import React , { useState } from 'react'
import {connect} from 'react-redux'
import {sendForm} from '../actions'

// function Form({dispatch, status = {enabled: false}}) {
function Form({dispatch, visible = false}) {
    let input = '';
    const [value, setValue] = useState("");
    const [area, setArea] = useState("");
    return (
        <div className={'form' + " " + (visible ? 'normal' : 'hidden')}>
            <br/>
            <div>
                <input value={value} onChange={e => setValue(e.target.value)}/>
            </div>
            <br/>
            <div>
                <textarea value={area} onChange={e => setArea(e.target.value)}/>
            </div>
            <div>
                <button
                    type="submit"
                    onClick={e => {
                        e.preventDefault();
                        dispatch(sendForm(value,area));
                    }}
                >Create
                </button>
            </div>
        </div>
    );
}

export default connect()(Form)
