import React from 'react'
import uuid from 'uuid/v4';

const posts = (state = [], action) => {
    switch (action.type) {
        case 'SEND_FORM':
            console.log(state);
            if (action.input && action.area) {
                return [
                    ...state,
                    {
                        id: uuid(),
                        title: action.input,
                        text: action.area
                    }
                ];
            }
            return state;
        case 'DELETE_POST':
            return state.filter(post => post.id !== action.id);
        case 'UPDATE_POST':
            console.log('UPDATE_POST');
            console.log(state);
            if (action.input && action.area) {
                return [
                    ...state,
                    {
                        id: uuid(),
                        title: action.input,
                        text: action.area
                    }
                ];
            }
        default:
            return state
    }
};

export default posts
