import {connect} from 'react-redux'
import {deletePost, sendForm, showForm, showUpdateForm} from '../../actions'
import Button from '../../components/Buttons/Button'


const mapStateToProps = (state, ownProps) => ({
    showElement: ownProps.showElement,
    action: ownProps.action,
    result:  formState(state, ownProps.showElement) ? "Hide Form": "Show Form",
    postId: ownProps.postId ? ownProps.postId : ""
});

function formState(state, searchedFormElement) {
    let formState = state.form.filter(v => v.showElement === searchedFormElement).pop();
    if (!formState) {
        return false;
    }
    return formState.enabled
}

/** mapDispatchToProps
 * adds event to our prop. We can use it from Button component as well. */
// const mapDispatchToProps = (dispatch, ownProps) => ({
//     onClick: () => dispatch(ownProps.action)
// });

function mapDispatchToProps(dispatch, ownProps) {
    return {
        onClick: function () {
            let action = ownProps.action;
           switch (action) {
               case 'showForm': return  dispatch(showForm(ownProps.showElement));
               case 'sendForm': return  dispatch(sendForm(ownProps.title, ownProps.text));
               case 'deletePost': return  dispatch(deletePost(ownProps.postId));
               case 'showUpdateForm': return  dispatch(showUpdateForm(ownProps.postId, ownProps.title, ownProps.text));
           }

        }
    }
}

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(Button)
