import React from 'react'

const postViewPage = (state = [], action) => {
    switch (action.type) {
        case 'SHOW_POST_DETAIL_PAGE':
        default:
            return [{
                    id: action.id,
                    title: action.title,
                    text: action.text
                }];
    }
};

export default postViewPage
