import React from 'react'
import uuid from 'uuid/v4';

const posts = (state = [], action) => {
    switch (action.type) {
        case 'SEND_FORM':
            console.log(state);
            if (action.input && action.text) {
                return [
                    ...state,
                    {
                        id: uuid(),
                        title: action.input,
                        text: action.text
                    }
                ];
            }
            return state;
        case 'DELETE_POST':
            return state.filter(post => post.id !== action.id);
        case 'UPDATE_POST':
            console.log('UPDATE_POST');
            console.log(state);
           // console.log(action);
            if (action.optionalState) {
                let updatedPost = state.filter(post => post.id === action.optionalState.id);
                console.log(updatedPost);
                console.log(state);

               // console.log(action.optionalState.id);
                return [
                    ...state,
                    updatedPost
                ];
            }
        default:
            return state
    }
};

export default posts
