import React, {useState} from 'react'
import { Button as UiButton, Modal, Confirm as PopUp } from 'semantic-ui-react'
import RegisterForm from './RegisterForm'

function StartPage () {
    const [open, setOpen] = useState(false);
    const [result, setResult] = useState('show the modal to capture a result');

    return (
        <div className="App">
        <div className={'ui segment'}>
                <p>
                    Result: <em>{result}</em>
                </p>
                <UiButton onClick={() => setOpen('true')}>Login</UiButton>
                <UiButton onClick={() => setOpen('true')}>Register</UiButton>

                <PopUp
                    open= {open}
                    content= {<RegisterForm/>}
                    confirmButton= {false}
                    cancelButton='Close'
                    onCancel={() => {setResult('cancel');setOpen(false)}}
                />
            </div>
        </div>
        )

}

export default StartPage
