import React from 'react'

const button = (state = [], action) => {
    switch (action.type) {
        case 'BUTTON_CLICKED':
            console.log('button');
            //return state;
               let button = state.filter(b => b.id === action.id).pop();

               if(button) {
                   console.log(state);

                   return [
                       ...state,
                       {
                           id: action.id,
                           enabled: !button.enabled
                       }
                   ];
               }

                //  state.map(button => button.id === action.id ? button.enabled = !button.enabled : button);
                // return state;

            console.log(state);
            return [
                ...state,
                {
                    id: action.id,
                    enabled: true
                }
            ];

        default:
            return state
    }
};

export default button
