import React from 'react'

const postViewPage = (state = [], action) => {
    switch (action.type) {
        case 'SHOW_POST_DETAIL_PAGE':
            return [{
                id: action.data.id,
                title: action.data.title,
                text: action.data.text
            }];
        default:
           return state
    }
};

export default postViewPage
