import React, {useState} from 'react'
import {connect} from 'react-redux'
import {sendForm, showForm, updatePost} from '../actions'
import {Form as AreaForm, TextArea} from 'semantic-ui-react'


//return main form with various buttons
function Form({dispatch, status = {enabled: false}, optionalState}) {
    const [title, setTitle] = useState("");
    const [area, setArea] = useState("");
    console.log('__FORM____');
    console.log(status);
    return (
        <div className={'ui segment' + " " + (status.enabled ? 'normal' : 'hidden')}>
            <p>Post title</p>
            <div className={'ui input'}>
                <input className={'input'} value={title} onChange={e => setTitle(e.target.value)}/>
            </div>
            <p>Post data</p>
            <AreaForm>
                <TextArea placeholder='Tell us more' value={area} onChange={e => setArea(e.target.value)}/>
            </AreaForm>

            <div>
                <CurrentButton
                    dispatch={dispatch}
                    status={status}
                    title={title}
                    area={area}
                    optionalState={optionalState}
                />
            </div>
        </div>
    );
}

// return 'Create Post' or 'Update post' buttons in form
function CurrentButton({dispatch, status, title, area, optionalState}) {
    if (status && status.elementAction === 'create') {
        return (
            <button
                type="submit"
                className={'ui primary button'}
                onClick={ e => {
                    e.preventDefault();
                    dispatch(sendForm(title, area));
                    dispatch(showForm(status.showElement));
                }}>
                Create Post
            </button>
        );
    }
    return (
        <button
            type="submit"
            className={'ui primary button'}
            onClick={ e => {
                e.preventDefault();
                dispatch(updatePost(optionalState));
                // dispatch(updatePost(title, area));
            }}>
            Update Post
        </button>
    );
}

export default connect()(Form)
