import React, {useState, useEffect} from 'react'
import { Button as UiButton, Confirm } from 'semantic-ui-react'

function StartPage () {
    const [open, setOpen] = useState(false);
    const [result, setResult] = useState('show the modal to capture a result');
    return (
            <div>
                <p>
                    Result: <em>{result}</em>
                </p>
                <UiButton onClick={setOpen('true')}>Show</UiButton>
                <Confirm
                    open={open}
                    content='This is a custom message'
                    onCancel={setResult('cancel')}
                    onConfirm={setResult('confirm')}
                />
            </div>
        )

}

export default StartPage
