import React, {useState, useEffect} from 'react'
import {connect} from 'react-redux'
import {sendForm, showForm, updatePost} from '../actions'
import {Form as AreaForm, TextArea} from 'semantic-ui-react'

var set = false;
//return main form with various buttons
function Form({dispatch, status = {enabled: false}}) {
    console.log('__FORM____');
    // const [title, setTitle] = useState("");
    // const [text, setText] = useState("");
    let titleValue = null;
    let textValue = null;
    const [title, setTitle] = useState("");
    const [text, setText] = useState("");
    // const [updateTitle, setUpdateTitle] = useState("");
    // const [updateText, setUpdateText] = useState("");
    // if (!status.optionalState) {
    //     status.optionalState = {
    //         text: "text",
    //         title: "text"
    //     }
    // }
    useEffect(() => {
        if (status.optionalState) {
         //   if (!set) {
                setTitle(status.optionalState.title);
                setText(status.optionalState.text);
                set = true;
           // }
           //  setUpdateTitle(status.optionalState.title);
           //  setUpdateText(status.optionalState.text);
        }
    },[status.optionalState]);

    console.log(status);
    console.log(status.optionalState);
    return (
        <div className={'ui segment' + " " + (status.enabled ? 'normal' : 'hidden')}>
            <p>Post title</p>
            <div className={'ui input'}>
                <input className={'input'} value={title} onChange={e => setTitle(e.target.value)}/>
            </div>
            <p>Post data</p>
            <AreaForm>
                <TextArea placeholder='Tell us more' value={text} onChange={e => setText(e.target.value)}/>
            </AreaForm>

            <div>
                <CurrentButton
                    dispatch={dispatch}
                    status={status}
                    title={title}
                    text={text}
                    optionalState={status.optionalState}
                />
            </div>
        </div>
    );
}

// return 'Create Post' or 'Update post' buttons in form
function CurrentButton({dispatch, status, title, text, optionalState}) {
    if (status && status.elementAction === 'create') {
        return (
            <button
                type="submit"
                className={'ui primary button'}
                onClick={e => {
                    e.preventDefault();
                    dispatch(sendForm(title, text));
                    dispatch(showForm(status.showElement));
                }}>
                Create Post
            </button>
        );
    }
    return (
        <button
            type="submit"
            className={'ui primary button'}
            onClick={e => {
                e.preventDefault();
                dispatch(updatePost(optionalState));
                // dispatch(updatePost(title, area));
            }}>
            Update Post
        </button>
    );
}

export default connect()(Form)
