const button = (state = 0, action) => {
    switch (action.type) {
        case 'BUTTON_CLICKED':
            if(state.filter(button => button.id) === action.id){
                return [
                    ...state,
                    {
                        id: action.id,
                        enabled: !state[id].enabled
                    }
                ];
            }
            return [
                ...state,
                {
                    id: action.id,
                    enabled: true
                }
            ];

        default:
            return state
    }
};

export default button
