import React from 'react'
import uuid from 'uuid/v4';

const form = (state = [], action,) => {
    switch (action.type) {
        case 'SEND_FORM':
            if (action) {
                return [
                    ...state,
                    {
                        id: uuid(),
                        title: action.input,
                        text: action.area
                    }
                ];
            }
            return state;
        case 'DELETE_POST':
            // return [
            //     ...state,
            //     {
            //         id: uuid(),
            //         title: action.input,
            //         text: action.area
            //     }
            // ];
            return state.filter(post => post.id !== action.id);
        // console.log('DELETE_POST-----');
        // console.log(state);
        // console.log(action);
        default:
            return state
    }
};

export default form
