import {connect} from 'react-redux'
import Button from '../../components/Buttons/Button'


const mapStateToProps = (state, ownProps) => ({
    showElement: ownProps.showElement,
    action: ownProps.action,
    enabled: formState(state, ownProps.showElement)
});

function formState(state, element) {
    let formState = state.form.filter(v => v.showElement === element).pop();
    if (!formState) {
        return
    }
    return formState.enabled
}

/** mapDispatchToProps
 * adds event to our prop. We can use it from Button component as well. */

export default connect(
    mapStateToProps,
)(Button)
