import React, {useState} from 'react'
import {Form, Input} from 'semantic-ui-react'

/**
 * Registration/Login Form
 */
function RegisterForm() {

    const fields = {
        firstName: 'firstName',
        lastName: 'lastName',
        password: 'password',
        email: 'email',
        terms: 'terms',
    };

    const [formState, setFormState] = useState([]);

    console.log(formState);

    function addValue(key, value) {
        setFormState(
            [...formState.filter(el => el.key !== key),
                {
                    key: key,
                    value: value,
                    error: retrieveErrors(key, value)
                }
            ]
        );
    }

    function hasErrors(key) {
        let element = formState.filter(el => el.key === key).pop();
        if (element) return element.error;

    }



    function submitForm(e) {
        let checkRequiredFields = new Promise(function (resolve, reject){
            let checking = [];
            for (let field in fields) {
                if(formState.filter(el => el.key === field).length === 0){
                    //addValue(field, 0);
                    checking.push({
                        key: field,
                        value: '',
                        error: retrieveErrors(field, 0)
                    });
                }
            }
           resolve (
               setFormState([...formState,
                ...checking]
               )
            );
        });



        checkRequiredFields.then(()=> {
            console.log(formState.filter(el => el.error != false).length);
        });

        // if(formState.filter(el => el.error !== false).length === 0) {
        //     window.location.href = "/mainPage";
        // }
    }

    function retrieveErrors(key, value) {
        if (!value || value.length < 1) {
            return {
                content: 'this is required field',
                pointing: 'left'
            };
        }
        switch (key) {
            case fields.firstName:
                if (value.length < 5) {
                    return {
                        content: 'Not enough characters',
                        pointing: 'left'
                    };
                }
                return false;
            case fields.lastName:
                if (value.length < 2) {
                    return {
                        content: 'Not enough characters',
                        pointing: 'left'
                    };
                }
                return false;
            case fields.password:
                if (value.length < 4) {
                    return {
                        content: 'Not enough characters',
                        pointing: 'left'
                    };
                }
                return false;
            case fields.terms:
                if (!value) {
                    return {
                        content: 'You must agree to the terms and conditions',
                        pointing: 'left'
                    };
                }
                return false;
            case fields.email:
                if (!value.match('^([a-z0-9_-]+\\.)*[a-z0-9_-]+@[a-z0-9_-]+(\\.[a-z0-9_-]+)*\\.[a-z]{2,6}$')) {
                    return {
                        content: 'Incorrect email address',
                        pointing: 'left'
                    };
                }
                return false;
            default:
                return false
        }
    }

    // console.log(formState);

    return (
        <Form id={'register-ui-form'}>
            <Form.Group widths='equal'>
                <Form.Field
                    id='form-input-control-first-name'
                    control={Input}
                    width={8}
                    error={hasErrors(fields.firstName)}
                    onChange={e => addValue(fields.firstName, e.target.value)}
                    label='First name'
                    placeholder='First name'
                />
                <Form.Field
                    id='form-input-control-last-name'
                    error={hasErrors(fields.lastName)}
                    control={Input}
                    width={8}
                    onChange={e => addValue(fields.lastName, e.target.value)}
                    label='Last name'
                    placeholder='Last name'
                />
            </Form.Group>
            <Form.Group>
                <Form.Field
                    id='form-input-control-last-name'
                    error={hasErrors('password')}
                    control={Input}
                    width={8}
                    onChange={e => addValue('password', e.target.value)}
                    label='Password'
                    type='password'
                    placeholder='password'
                />
                <Form.Field
                    id='form-input-control-last-name'
                    error={hasErrors('email')}
                    control={Input}
                    width={8}
                    onChange={e => addValue('email', e.target.value)}
                    label='Email address'
                    type='email'
                    placeholder='email'
                />
            </Form.Group>
            <Form.Field>
                <Form.Checkbox
                    onClick={(e, {checked}) => addValue(fields.terms, checked)}
                    error={hasErrors(fields.terms)}
                    label='I agree to the Terms and Conditions'
                />
            </Form.Field>
            {/*<Link to="/mainPage">*/}
            <Form.Button
                content='Submit'
                onClick={e => submitForm(e)}
            />
            {/*<Button*/}
            {/*</Link>*/}
        </Form>
    )
}

export default RegisterForm
