import React from 'react'
import {connect} from "react-redux";
import {deletePost, showUpdateForm} from "../actions";
import { Icon } from 'semantic-ui-react'

function Post({dispatch,title,text,id}) {
    console.log("__POST__");
    return (
        <div className={'ui segment'}>
            <h4 id={'post-title'}>{title}</h4>
            <p id={'post-text-content'}>{text}</p>
            <div id={'post-buttons'}>
                <div className={'ui right floated animated button'}
                     onClick={e => {
                         e.preventDefault();
                         dispatch(deletePost(id));
                     }}>
                    <div className="visible content">Delete</div>
                    <div className="hidden content">
                        <Icon link name='close'/>
                    </div>
                </div>
                {/*<div className={'ui right floated animated button'}*/}
                {/*     onClick={e => {*/}
                {/*         e.preventDefault();*/}
                {/*         dispatch(showUpdateForm(id, title, text));*/}
                {/*     }}>*/}
                {/*    <div className="visible content">Update</div>*/}
                {/*    <div className="hidden content">*/}
                {/*        <Icon link name='close'/>*/}
                {/*    </div>*/}
                {/*</div>*/}
                <div className="ui floating labeled icon dropdown button">
                    <span className="text">Menu</span>
                    <div className="menu">
                        <div className="item">
                            <i className="left dropdown icon">-</i>
                            <span className="text">Left</span>
                            <div className="left menu">
                                <div className="item">1</div>
                                <div className="item">2</div>
                                <div className="item">3</div>
                            </div>
                        </div>
                        <div className="item">
                            <span className="text">Right</span>
                            <div className="right menu">
                                <div className="item">1</div>
                                <div className="item">2</div>
                                <div className="item">3</div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

        </div>
    );
}

export default connect()(Post)
