import React, {useState} from 'react'
import {connect} from 'react-redux'
import {sendForm, showForm} from '../actions'

function Form({dispatch, status = {enabled: false}}) {
    let input = '';
    const [value, setValue] = useState("");
    const [area, setArea] = useState("");
    console.log(status);
    return (
        <div className={'ui segment' + " " + (status.enabled ? 'normal' : 'hidden')}>
            <p>Post title</p>
            <div className={'ui input'}>
                <input className={'input'} value={value} onChange={e => setValue(e.target.value)}/>
            </div>
            <p>Post data</p>
            <div className={'ui form'}>
                {/*<textarea rows="3"  value={area} onChange={e => setArea(e.target.value)}/>*/}
                <TextArea placeholder='Tell us more' />
            </div>
            <div>
                <button
                    type="submit"
                    className={'ui primary button'}
                    onClick={e => {
                        e.preventDefault();
                        dispatch(sendForm(value, area));
                        dispatch(showForm(status.showElement));
                    }}
                >Create
                </button>
            </div>
        </div>
    );
}

export default connect()(Form)
