const button = (state = [], action) => {
    switch (action.type) {
        case 'BUTTON_CLICKED':
            if(state.filter(button => button.id === action.id)){
                console.log(state);
                return state.map(button => button.id === action.id ? button.enabled = !button.enabled : button);
                // return [
                //     ...state,
                //     {
                //         id: action.id,
                //         enabled: !button.enabled
                //     }
                // ];
            }
            console.log(state)
            return [
                ...state,
                {
                    id: action.id,
                    enabled: true
                }
            ];

        default:
            return state
    }
};

export default button
