import React, {useState} from 'react'
import {connect} from 'react-redux'
import {sendForm, showForm, updatePost} from '../actions'
import {Form as AreaForm, TextArea} from 'semantic-ui-react'


//return main form with various buttons
function Form({dispatch, status = {enabled: false }}) {
    console.log('__FORM____');
    const [title, setTitle] = useState("");
    const [text, setText] = useState("");
    let titleValue = null;
    let textValue = null;
    if (status.optionalState) {
         titleValue = status.optionalState.title;
         textValue = status.optionalState.text;
    }
    console.log(status);
    console.log(status.optionalState);
    return (
        <div className={'ui segment' + " " + (status.enabled ? 'normal' : 'hidden')}>
            <p>Post title</p>
            <div className={'ui input'}>
                <input className={'input'} value={titleValue ? titleValue : title} onChange={e => setTitle(e.target.value)}/>
            </div>
            <p>Post data</p>
            <AreaForm>
                <TextArea placeholder='Tell us more' value={textValue ? textValue : text} onChange={e => setText(e.target.value)}/>
            </AreaForm>

            <div>
                <CurrentButton
                    dispatch={dispatch}
                    status={status}
                    title={title}
                    text={text}
                    optionalState={status.optionalState}
                />
            </div>
        </div>
    );
}

// return 'Create Post' or 'Update post' buttons in form
function CurrentButton({dispatch, status, title, text, optionalState}) {
    if (status && status.elementAction === 'create') {
        return (
            <button
                type="submit"
                className={'ui primary button'}
                onClick={ e => {
                    e.preventDefault();
                    dispatch(sendForm(title, text));
                    dispatch(showForm(status.showElement));
                }}>
                Create Post
            </button>
        );
    }
    return (
        <button
            type="submit"
            className={'ui primary button'}
            onClick={ e => {
                e.preventDefault();
                dispatch(updatePost(optionalState));
                // dispatch(updatePost(title, area));
            }}>
            Update Post
        </button>
    );
}

export default connect()(Form)
