import React, {useState, useEffect} from 'react'
import {sendForm} from '../actions'

function Form(dispatch,{ status = {enabled: false}, onClick}) {
    let input;
    console.log(input);
    return (
        <div className={'form' + " " + (status.enabled ? 'normal' : 'hidden')}>
            <br/>
            <div>
                <input ref={node => (input = node)}/>
            </div>
            <br/>
            <textarea/>
            <div>
                <button
                    type="submit"
                    onClick={e => {
                        e.preventDefault();
                        if (!input.value.trim()) {
                            return
                        }
                        dispatch(sendForm(input.value));
                        input.value = ''
                    }}
                >Create
                </button>
            </div>
        </div>
    );
}

export default Form
