import React from 'react';
import '../../App.css';
import Form from '../../containers/Form';
import Button from '../../containers/Buttons/Button';
import PostList from '../../containers/PostList';
import {Link} from "react-router-dom";
import {connect} from 'react-redux'
import 'react-fontawesome';
import {Button as UiButton, Popup, Grid} from 'semantic-ui-react'

/** Home page  */
function PostViewPage({state}) {
    console.log(state);

    return (
        <div className="App">
            <div id={"Header"}>
                <div id={'user-area'} className={'ui segment'}>
                    <Link to="/">
                        <UiButton>Back</UiButton>
                    </Link>
                </div>
            </div>
            <br/>
            <div className="ui raised very padded text container segment">
                <h2 className="ui header">{state.title}</h2>
                <p>
                    {state.text}
                </p>

            </div>
        </div>
    );
}

export default PostViewPage;
