function mapStateToProps(state, ownProps) {
    return {
        showElement: ownProps.showElement ? ownProps.showElement : false,
        action: ownProps.action,
        result: getButtonText(state, ownProps),
        postId: ownProps.postId ? ownProps.postId : false,
        buttonText: ownProps.buttonText ? ownProps.buttonText : false
    }

}

function getButtonText(state, ownProps) {
    // if (!ownProps.showElement) {
    //     return ownProps.buttonText
    // }
    let formState = state.form.filter(v => v.showElement === ownProps.showElement).pop();
    if (!formState) {
        return ownProps.buttonText
    }
    return formState.enabled ? "Hide Form" : "Show Form"
}

/** mapDispatchToProps
 * adds event to our prop. We can use it from Button component as well. */
// const mapDispatchToProps = (dispatch, ownProps) => ({
//     onClick: () => dispatch(ownProps.action)
// });

function mapDispatchToProps(dispatch, ownProps) {
    return {
        onClick: function () {
            let action = ownProps.action;
            switch (action) {
                case 'showForm':
                    return dispatch(showForm(ownProps.showElement));
                case 'createPost':
                    return dispatch(createPost(ownProps.title, ownProps.text));
                case 'deletePost':
                    return dispatch(deletePost(ownProps.postId));
                case 'updatePost':
                    return dispatch(updatePost(ownProps.title, ownProps.text, status.optionalState.id));
                case 'showUpdateForm':
                    return dispatch(showUpdateForm(ownProps.postId, ownProps.title, ownProps.text));
            }
        }
    }
}

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(Button)
