import React from 'react'
import {connect} from "react-redux";
import {deletePost, showUpdateForm} from "../actions";
import {Popup,Button as UiButton, Grid} from 'semantic-ui-react'
import Button from '../containers/Buttons/Button';
import {Link} from "react-router-dom";

/** Post component */
function Post({title, text, id}) {
    let data = {'title': title,'text': text,'id':id};
    return (
        <div className={'ui segment'}>
            <h4 id={'post-title'}>{title}</h4>
            <p id={'post-text-content'}>{text}</p>
            <div id={'post-buttons'}>
                <Button
                    action={'deletePost'}
                    buttonText={'DELETE'}
                    postId={id}
                />
                <UpdateShowPopup
                    data={data}
                    />
            </div>
        </div>
    );
}

/** Button with popup options */
function UpdateShowPopup({data}) {
    return (
        <Popup
            trigger={<UiButton className={' ui button ui right floated  button'}>. . .</UiButton>}
            flowing hoverable>
            <Grid centered divided columns={2}>
                <Grid.Column textAlign='center'>
                    <Button
                        action={'showUpdateForm'}
                        buttonText={'UPDATE'}
                        title={data.title}
                        text={data.text}
                        postId={data.id}
                    />
                </Grid.Column>
                <Grid.Column textAlign='center'>
                    <Link to="/post">
                        <Button>Show</Button>
                    </Link>
                </Grid.Column>
            </Grid>
        </Popup>
    );
}


export default connect()(Post)
