import React from 'react'
import {connect} from "react-redux";
import {deletePost, showUpdateForm} from "../actions";
import { Icon } from 'semantic-ui-react'

function Post({dispatch,title,text,id}) {
    console.log("__POST__");
    return (
        <div className={'ui segment'}>
            <h4 id={'post-title'}>{title}</h4>
            <p id={'post-text-content'}>{text}</p>
            <div id={'post-buttons'}>
                <div className={'ui right floated animated button'}
                     onClick={e => {
                         e.preventDefault();
                         dispatch(deletePost(id));
                     }}>
                    <div className="visible content">Delete</div>
                    <div className="hidden content">
                        <Icon link name='close'/>
                    </div>
                </div>
                {/*<div className={'ui right floated animated button'}*/}
                {/*     onClick={e => {*/}
                {/*         e.preventDefault();*/}
                {/*         dispatch(showUpdateForm(id, title, text));*/}
                {/*     }}>*/}
                {/*    <div className="visible content">Update</div>*/}
                {/*    <div className="hidden content">*/}
                {/*        <Icon link name='close'/>*/}
                {/*    </div>*/}
                {/*</div>*/}
                <div className="ui dropdown">
                    <div className="text">File</div>
                    <i className="dropdown icon"></i>
                    <div className="menu">
                        <div className="item">New</div>
                        <div className="item">
                            <span className="description">ctrl + o</span>
                            Open...
                        </div>
                        <div className="item">
                            <span className="description">ctrl + s</span>
                            Save as...
                        </div>
                        <div className="item">
                            <span className="description">ctrl + r</span>
                            Rename
                        </div>
                        <div className="item">Make a copy</div>
                        <div className="item">
                            <i className="folder icon"></i>
                            Move to folder
                        </div>
                        <div className="item">
                            <i className="trash icon"></i>
                            Move to trash
                        </div>
                        <div className="divider"></div>
                        <div className="item">Download As...</div>
                        <div className="item">
                            <i className="dropdown icon"></i>
                            Publish To Web
                            <div className="menu">
                                <div className="item">Google Docs</div>
                                <div className="item">Google Drive</div>
                                <div className="item">Dropbox</div>
                                <div className="item">Adobe Creative Cloud</div>
                                <div className="item">Private FTP</div>
                                <div className="item">Another Service...</div>
                            </div>
                        </div>
                        <div className="item">E-mail Collaborators</div>
                    </div>
                </div>
            </div>

        </div>
    );
}

export default connect()(Post)
